b4peWtS82m3su5q1.command:
// Macro to create a random NPC, being sent in a whisper to GMs on the server.

const namesTableID = game.tables.find(o => o.name === "The Lost Citadel: Beastmen Names")._id;
const namesTable = await game.tables.get(namesTableID);
const namesResult = await namesTable.draw({displayChat: false});
const names = namesResult.results.pop().getChatText();

const appearanceTableID = game.tables.find(o => o.name === "The Lost Citadel: Beastmen Appearances")._id;
const appearanceTable = await game.tables.get(appearanceTableID);
const appearanceResult = await appearanceTable.draw({displayChat: false});
const appearance = appearanceResult.results.pop().getChatText();

const behaviorTableID = game.tables.find(o => o.name === "The Lost Citadel: Beastmen Behaviors")._id;
const behaviorTable = await game.tables.get(behaviorTableID);
const behaviorResult = await behaviorTable.draw({displayChat: false});
const behavior = behaviorResult.results.pop().getChatText();

const message = `<p>A <b>${appearance}</b> @UUID[Compendium.shadowdark.monsters.Actor.k8tAgECWIzPIStGU]{Beastman} named <b>${names}</b> 
is <b>${behavior}</b>.</p>`;

const chatData = {
  user: game.user._id,
  speaker: ChatMessage.getSpeaker(),
  content: message,
  whisper: game.users.filter(u => u.isGM).map(u => u._id),
};

ChatMessage.create(chatData, {});

